import React, { useContext } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { baseUrl } from "../Components/Shared";
import { LoginContext } from "../App";

function Logout() {
	const navigate = useNavigate();
	const location = useLocation();
	const [loggedIn, setLoggedIn] = useContext(LoginContext);

	return (
		<button
			className="bg-gray-500 px-3 py-1 hover:bg-gray-600 text-white rounded-sm"
			onClick={(e) => {
				const url = baseUrl + "api/customers/";
				fetch(url, {
					headers: {
						"Content-Type": "application/json",
						Authorization: "Bearer " + localStorage.clear("access"), //Bearar gap is importent
					},
				})
					.then((response) => {
						if (response.status === 401) {
							setLoggedIn(false);
						}
						if (!response.ok) {
							throw new Error("Page Logout..");
						}
					})
					.catch((e) => {
						console.log(e);
					});
			}}
		></button>
	);
}

export default Logout;
