import Employes from "./Components/Employes";
import Detalis from "./Components/Details.json";
import { useState } from "react";
// const { v4: uuidv4 } = require("uuid");

function App() {
  const [details, setDetails] = useState(Detalis.Details);

  function updateEmployee(id, newNeme, newRole) {
    console.log("updateEmployee add inside of app.js");
    console.log(id);
    console.log(newNeme, newRole);
    const updateEmployee = details.map((employee) => {
      if (id == employee.id) {
        return { ...employee, name: newNeme, role: newRole };
      }
    });

    setDetails(updateEmployee);
  }

  return (
    <div className="w-8/12 py-10 px-2 h-screen bg-gray-100 mx-auto flex justify-between  flex-wrap">
      <>
        {details.map((details) => (
          <Employes
            key={details.id}
            id={details.id}
            name={details.name}
            role={details.role}
            img={details.img}
            updateEmployee={updateEmployee}
          />
        ))}
      </>
    </div>
  );
}

export default App;
