import React, { useEffect, useState } from "react";

function Definition() {
  const [word, setWord] = useState([]);

  useEffect(() => {
    fetch("https://api.dictionaryapi.dev/api/v2/entries/en/helicopter")
      .then((response) => response.json())
      .then((data) => {
        setWord(data[0].meanings);
        console.log(data[0].meanings);
      });
  }, []);

  return (
    <div className="p-20">
      <h1 className="text-blue-800">Here is a Definition</h1>

      {word.map((meaning) => (
        <p className="text-green-700">{meaning.definitions[0].definition}</p>
      ))}

      {word.map((e) => {
        return (
          <p>
            {" "}
            {e.partOfSpeech}:{e.definitions[0].definition}
          </p>
        );
      })}
    </div>
  );
}

export default Definition;
