import { useParams, Link, useNavigate } from "react-router-dom";
import React, { useEffect, useState } from "react";
import { baseUrl } from "../Components/Shared";

function Customer() {
	const { id } = useParams();
	const navigate = useNavigate();
	const [customer, setCustomer] = useState();
	const [tempCustomer, setTempCustomer] = useState();
	const [notFound, setNotFound] = useState();
	const [change, setChange] = useState(false);

	useEffect(() => {
		if (!customer) return;
		if (!customer) return;

		let equal = true;
		if (customer.name !== tempCustomer.name) equal = false;
		if (customer.industry !== tempCustomer.industry) equal = false;

		if (equal) setChange(false);
	});

	useEffect(() => {
		const url = baseUrl + "api/customers/" + id;

		fetch(url)
			.then((response) => {
				if (response.status === 404) {
					setNotFound(true);
				}
				return response.json();
			})
			.then((data) => {
				setCustomer(data.customer);
				setTempCustomer(data.customer);
			});
	}, [id]);

	function updateCustomer(params) {
		const url = baseUrl + "api/customers/" + id;

		fetch(url, {
			method: "POST",
			headers: { "Content-Type": "application/json" },
			body: JSON.stringify(tempCustomer),
		})
			.then((response) => {
				console.log("response :", response);
				if (re) {
				}
				return response.json();
			})
			.then((data) => {
				setCustomer(data.customer);
				setChange(false);
				console.log(data);
			})
			.catch((e) => {
				console.log(e);
			});
	}

	return (
		<div className="p-20">
			{notFound ? (
				<p className="text-2xl">The Customer with id {id} was not found</p>
			) : null}
			{customer ? (
				<div className="">
					<input
						className="mb-2 px-1 h-8 block"
						value={tempCustomer.name}
						onChange={(e) => {
							setTempCustomer({ ...tempCustomer, name: e.target.value });
							setChange(true);
						}}
					/>

					<input
						className="mb-4 px-1 h-8 block"
						value={tempCustomer.industry}
						onChange={(e) => {
							setTempCustomer({ ...tempCustomer, industry: e.target.value });
							setChange(true);
						}}
					/>

					{change ? (
						<>
							<button
								onClick={updateCustomer}
								className="px-10 py-2 bg-gray-300 rounded-sm mr-3 active:bg-slate-500"
							>
								Save
							</button>

							<button
								onClick={(e) => {
									setTempCustomer({ ...customer });
									setChange(false);
								}}
								className="px-10 py-2 bg-gray-300 rounded-sm mb-3 active:bg-slate-500"
							>
								Cancel
							</button>
						</>
					) : null}

					<button
						className="bg-red-400 px-4 py-2 mb-2 active:bg-red-300"
						onClick={(e) => {
							const url = baseUrl + "api/customers/" + id;
							fetch(url, {
								method: "DELETE",
								headers: { "Content-Type": "application/json" },
							})
								.then((response) => {
									if (!response.ok) {
										throw new Error("Something went worng...");
									}
									navigate("/customers");
								})
								.catch((e) => {
									console.log(e);
								});
						}}
					>
						Delete
					</button>
				</div>
			) : null}
			<br />

			<Link to="/customers">Go back.</Link>
			<p> </p>
		</div>
	);
}

export default Customer;
