import { useEffect, useState } from "react";

function UseFatch(url) {
	const [data, setData] = useState();

	useEffect(() => {
		fetch(url)
			.then((response) => {
				return response.json();
			})
			.then((data) => {
				setLoading(false);
			})
			.catch((e) => {
				setError(e);
				console.log(e);
			});
	}, [url]);

	return data;
}

export default UseFatch;
