import Employes from "./Components/Employes";
import Detalis from "./Components/Details.json";
import { useState } from "react";
import AddEmploye from "./Components/AddEmploye ";
const { v4: uuidv4 } = require("uuid");

function App() {
  const [details, setDetails] = useState(Detalis.Details);

  function updateEmployee(id, newNeme, newRole) {
    const updateEmployee = details.map((employee) => {
      if (employee.id === id) {
        return { ...employee, name: newNeme, role: newRole };
      }
      return employee;
    });

    setDetails(updateEmployee);
  }

  function newEmployee(name, role, img) {
    const newEmployee = {
      id: uuidv4(),
      name: name,
      role: role,
      img: img,
    };

    setDetails([...details, newEmployee]);
  }

  return (
    <div className="w-8/12 py-10 px-2 bg-gray-100 mx-auto flex flex-col-3 gap-2 justify-center flex-wrap">
      <>
        {details.map((details) => (
          <Employes
            key={uuidv4()}
            id={details.id}
            name={details.name}
            role={details.role}
            img={details.img}
            updateEmployee={updateEmployee}
          />
        ))}
      </>
      <AddEmploye newEmployee={newEmployee} />
    </div>
  );
}

export default App;
